    <!-- โหลด Page ด้วย JavaScript -->
    <script>
        // Header
        fetch('header.php') // เปลี่ยนเป็น .php
            .then(response => response.text())
            .then(data => {
                document.getElementById('header-placeholder').innerHTML = data;
            })
            .catch(error => {
                console.error('Error loading header:', error);
                document.getElementById('header-placeholder').innerHTML = '<p>เกิดข้อผิดพลาดในการโหลดเมนู</p>';
            });

        // Footer
        fetch('footer.php') // เปลี่ยนเป็น .php
            .then(response => response.text())
            .then(data => {
                document.getElementById('footer-placeholder').innerHTML = data;
            })
            .catch(error => {
                console.error('Error loading footer:', error);
            });

        // ดักจับ Referrer URL เพิ่มใหม่ล่าสุด อย่าลืมลบคำนี้ออกด้วย
        const referrer = document.referrer;
        let keyword = '';
        let searchEngine = '';

        // ตรวจสอบว่า Referrer มาจากเครื่องมือค้นหาหรือไม่
        if (referrer.includes('google.com')) {
            searchEngine = 'Google';
            const urlParams = new URLSearchParams(referrer.split('?')[1]);
            keyword = urlParams.get('q') || '';
        } else if (referrer.includes('bing.com')) {
            searchEngine = 'Bing';
            const urlParams = new URLSearchParams(referrer.split('?')[1]);
            keyword = urlParams.get('q') || '';
        }

        // ดักจับพฤติกรรมผู้ใช้
        let startTime = new Date();
        let bounced = true; // สมมติว่าเด้งกลับ

        // ถ้าผู้ใช้คลิกหรือเลื่อนหน้า ถือว่าไม่เด้งกลับ
        document.addEventListener('click', () => bounced = false);
        document.addEventListener('scroll', () => bounced = false);

        // เมื่อผู้ใช้ปิดหน้าเว็บหรือเปลี่ยนหน้า
        window.addEventListener('beforeunload', () => {
            const endTime = new Date();
            const dwellTime = Math.round((endTime - startTime) / 1000); // ระยะเวลาในวินาที

            // ส่งข้อมูลไปยัง back-end
            fetch('track_referral.php', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    page_url: window.location.pathname,
                    keyword: keyword,
                    referrer: referrer,
                    dwell_time: dwellTime,
                    bounced: bounced
                })
            });
        });
    </script>

